#include <iostream>
#include <string>
#include "include/InfInt.h"

//this is an overkill of digits, the period must be less then 999, but I'm a little lazy on optimizing this working solution
InfInt one = "";
using namespace std;

int main(){
	InfInt result;
	int max = 6;
	int currBest = 7;
	for(int i = 11; i < 1000; i++){
		if(one % i != 0){  //this saves a few iterations. could be improved but again optimizing this solution seems unnecessary
			result = one / i;
			string compare = result.toString();
			bool found = false;
			int count = 100;
			while(!found){
				if(compare.substr(10,100) == compare.substr(count,100)){	//100 is an arbitrary number. this solution breaks down if there are any substrings of > 99 length that are the same.
					found = true;											//for instance, this algorithm would return a cycle length in pi, when in reality there are no cycles.
					if(count - 20 > max){
						max = count-10;
						currBest = i;
					}
				}
				count++;
			}
		}
	}
	cout << currBest << " : " << max;
}